#[cfg(test)]
mod tests {

    extern crate oqs;
    extern crate rustc_serialize;
        
    
    use self::oqs::*;
    use self::rustc_serialize::hex::{ToHex, FromHex};
    
    #[test]
    fn basic_kyber_test() {
        let kemalg = kem::Kem::new(kem::Algorithm::Kyber1024).unwrap();
        let (kem_pk, kem_sk) = kemalg.keypair().unwrap();
        let (kem_ct, b_kem_ss) = kemalg.encapsulate(&kem_pk).unwrap();
        let a_kem_ss = kemalg.decapsulate(&kem_sk, &kem_ct).unwrap();
        assert_eq!(a_kem_ss, b_kem_ss);
    }

    #[test]
    fn static_kyber_test() {
        let kemalg = kem::Kem::new(kem::Algorithm::Kyber1024).unwrap();

        let alice_public_key_bytes = "".from_hex().unwrap();
        let alice_private_key_bytes = "".from_hex().unwrap();

        let alice_public_key = kemalg.public_key_from_bytes(&alice_public_key_bytes).unwrap();
        let _alice_private_key = kemalg.secret_key_from_bytes(&alice_private_key_bytes).unwrap();
        
        let (ciphertext, shared_secret) = kemalg.encapsulate(&alice_public_key).unwrap();

        println!("Bob Kyber1024 KEM ciphertext: {}\n", ciphertext.into_vec().to_hex());

        println!("Bob shared secret: {}\n", shared_secret.into_vec().to_hex())
    }    
}
